import { AppUI } from './AppUI';
import './App.css';
import React from "react";

const DefaultTodos =[
{text: "Dar de comer al perro", completed: false},
{text: "Poner a cocer los frijoles", completed: true},
{text: "Sacar a Pasear al Perro", completed: false},
{text: "lalalal", completed: false},
{text: "lavar la ropa", completed:true}
]

function useLocalStorage(itemName, initialValue){
  const [error, setError] = React.useState(false);
  const[loading,setLoading] = React.useState(true);
  const [item, setItem]  = React.useState(initialValue);


  React.useEffect(()=>{
    setTimeout(()=>{
      try {
      const localStorageItem = localStorage.getItem(itemName);
  let parsedItem = initialValue;

      if (!localStorageItem) {
          localStorage.setItem(itemName, JSON.stringify(initialValue));
       }else{
           parsedItem = JSON.parse(localStorageItem);
  } 
      setItem(parsedItem);
      setLoading(false);
} catch(error){
  setError(error);
}
    },1000);
  });

    const saveItem = (newItem) =>{
      try{ 
    const stringifiedItem = JSON.stringify(newItem);
    localStorage.setItem(itemName, stringifiedItem);
    setItem(newItem);
  } catch(error){
    setError(error);
  }
};
  return {
  item,
  saveItem,
  loading,
  error
};
};

function App() {
  const {
    item: todos,
    saveItem : saveTodos,
    loading,
    error,
  }= useLocalStorage("TODOS_V1", []);



  const completedTodos = todos.filter(todo => !!todo.completed).length;
  const totalTodos = todos.length;
  const [searchValue, setSearchValue] = React.useState("");
  let searchedString = [];

    if (!searchValue.length >= 1) {
    searchedString = todos;
  }
  else {
    searchedString = todos.filter(
      todo => {
        const todoText = todo.text.toLowerCase();
        const searchText = searchValue.toLowerCase();
        return (
          todoText.includes(searchText)
        )
      }
    )
  }

  const completeTodo = (text) => {
    const todoIndex = todos.findIndex(todo => todo.text === text);
    const newTodos = [...todos];
    newTodos[todoIndex].completed = true;
    saveTodos(newTodos);
  };
  const deleteTodo = (text) =>{
    const todoIndex = todos.findIndex(todo => todo.text === text);
    const newTodos = [...todos];
    newTodos.splice(todoIndex, 1);
    saveTodos(newTodos);
  };

//   console.log("render antes del useeffect");

// React.useEffect(()=>{
//   console.log("use effect");
// },[totalTodos]);
// console.log("render despues del useeffect");


  return (
    <AppUI
    totalTodos = {totalTodos}
    completedTodos={completedTodos}
    searchValue = {searchValue}
    setSearchValue = {setSearchValue}
    completeTodo = {completeTodo}
    deleteTodo= {deleteTodo}
    searchedString = {searchedString}
    loading = {loading}
    error = {error}
    />
  );
  }
export default App;
