import React from "react";
import "./TodoItem.css"


function TodoItem(props){

    const onDelete = ()=>{
        alert("Borraste "+ props.text);
    } 


    return(
        <li className="TodoItem">
        <span
            onClick={props.onComplete}
        >🦈</span>
            <p> {props.text} </p>
       <span
            onClick={onDelete}
       >🐷</span>
       </li>
    )
}

export {TodoItem};
