import { TodoCounter } from "./TodoCounter";
import { TodoList } from "./TodoList";
import { TodoItem } from "./TodoItem";
import { TodoSearch } from "./TodoSearch";
import { CreateTodoButton } from "./CreateTodoButton";
import './App.css';
import React from "react";

const todos =[
{text: "Dar de comer al perro", completed: false},
{text: "Poner a cocer los frijoles", completed: true},
{text: "Sacar a Pasear al Perro", completed: true},
{text: "lalalal", completed: false}
]
function App() {
  const [newTodos, setTodos]  = React.useState(todos);
  const completedTodos = newTodos.filter(newTodos => newTodos.completed).length;
  const totaltodos = newTodos.length;
  const [searchValue, setSearchValue] = React.useState("");


  return (
  <React.Fragment>
    <TodoCounter/>
      total = {totaltodos};
      completed={completedTodos}
  <TodoSearch
      searchValue = {searchValue}
      setSearchValue = {setSearchValue}

  />

  <TodoList>
    {todos.map(todo => (
    <TodoItem key={todo.text} text={todo.text}/>
    ))}

  </TodoList>
  <CreateTodoButton/>

  </React.Fragment>    
  );  
}

export default App;
