import { TodoCounter } from "./TodoCounter";
import { TodoList } from "./TodoList";
import { TodoItem } from "./TodoItem";
import { TodoSearch } from "./TodoSearch";
import { CreateTodoButton } from "./CreateTodoButton";
import './App.css';
import React from "react";

const DefaultTodos =[
{text: "Dar de comer al perro", completed: false},
{text: "Poner a cocer los frijoles", completed: true},
{text: "Sacar a Pasear al Perro", completed: false},
{text: "lalalal", completed: false},
{text: "lavar la ropa", completed:true}
]



function App() {
  const [todos, setTodos]  = React.useState(DefaultTodos);
  const completedTodos = todos.filter(todo => !!todo.completed).length;
  const totalTodos = todos.length;
  const [searchValue, setSearchValue] = React.useState("");
  let searchedString = [];
  if (!searchValue.length >= 1) {
    searchedString = todos;
  }
  else {
    searchedString = todos.filter(
      todo => {
        const todoText = todo.text.toLowerCase();
        const searchText = searchValue.toLowerCase();
        return (
          todoText.includes(searchText)
        )
      }
    )
  }
  const completeTodo = (text) => {
    const todoIndex = todos.findIndex(todo => todo.text === text);
    const newTodos = [...todos];
    newTodos[todoIndex].completed = true;
    setTodos(newTodos);
  };
  const deleteTodo = (text) =>{
    const todoIndex = todos.findIndex(todo => todo.text === text);
    const newTodos = [...todos];
    newTodos.splice(todoIndex, 1);
    setTodos(newTodos);
  };
  return (
  <React.Fragment>
    <TodoCounter
    total = {totalTodos}
    completed={completedTodos}
    />

  <TodoSearch
      searchValue = {searchValue}
      setSearchValue = {setSearchValue}

  />

  <TodoList>
    {searchedString.map(todo => 
    (
    <TodoItem 
    key={todo.text} 
    text={todo.text}
    onComplete = {() => completeTodo(todo.text)}
    onDelete = {() => deleteTodo(todo.text)
    }
    /> 
    ))

    }

  </TodoList>
  <CreateTodoButton/>

  </React.Fragment>    
  );  
}

export default App;
